const nav=document.querySelector("nav.scroll-animate");let lastScrollTop=document.documentElement.scrollTop;const scrollNav=()=>{let curentScrollTop=document.documentElement.scrollTop;if(curentScrollTop>lastScrollTop){nav.style.top="-100px"}else{nav.style.top="0"}lastScrollTop=curentScrollTop};window.addEventListener("scroll",scrollNav);const menuUnfold=document.querySelector(".menu-unfold");let clickOutMenuChangeStatus=e=>{if(e.target.matches(".menu")&&!menuUnfold.classList.contains("menu-unfold-true")){menuUnfold.classList.toggle("menu-unfold-true")}else if(!e.target.matches(".menu-unfold")&&menuUnfold.classList.contains("menu-unfold-true")){menuUnfold.classList.toggle("menu-unfold-true")}};window.addEventListener("click",clickOutMenuChangeStatus);import darkreader from"https://cdn.jsdelivr.net/npm/darkreader@4.9.58/+esm";function darkReaderInit(){const darkReaderButton=document.createElement("button");darkReaderButton.className="darkreader-button";darkReaderButton.setAttribute("aria-label","Activate dark mode");darkReaderButton.setAttribute("aria-checked","false");darkReaderButton.setAttribute("role","checkbox");document.body.appendChild(darkReaderButton);darkReaderButton.addEventListener("click",darkReaderEvent);darkreader.auto(false)}function darkReaderEvent(e){e.preventDefault();const darkReaderButton=e.target;darkReaderButton.setAttribute("aria-checked",darkReaderButton.getAttribute("aria-checked")==="true"?"false":"true");if(darkReaderButton.getAttribute("aria-checked")==="true"){document.body.classList.remove("background-fill-light");document.body.classList.add("background-fill-dark");darkreader.enable({brightness:100,contrast:90,sepia:10})}else{document.body.classList.remove("background-fill-dark");document.body.classList.add("background-fill-light");darkreader.disable()}}window.addEventListener("load",darkReaderInit);