const cardsMainContainer=document.getElementsByClassName("cards__main-container")[0],cards=document.getElementsByClassName("card__container");let counter=0;const updateRate=10,isTimeToUpdate=()=>counter++%10==0,mouse={_x:0,_y:0,x:0,y:0,updatePosition:function(t){let e=t;this.x=e.clientX-this._x,this.y=-((e.clientY-this._y)*1)},setOrigin:function(t){this._x=t.offsetLeft+Math.floor(t.offsetWidth/2),this._y=t.offsetTop+Math.floor(t.offsetHeight/2)},show:function(){return"("+this.x+", "+this.y+")"}};mouse.setOrigin(cardsMainContainer);const update=t=>{mouse.updatePosition(t);for(let e=0;e<cards.length;e++){if(t.target.closest(".card__container")===cards[e]){""!==t.target.closest(".card__container").style.transform&&removeTransformStyle(cards[e]);continue}let s=(mouse.y/cards[e].offsetHeight/2).toFixed(2),o=(mouse.x/cards[e].offsetWidth/2).toFixed(2);s>.2&&(s=.2),s<-.2&&(s=-.2),o>.2&&(o=.2),o<-.2&&(o=-.2),updateTransformStyle(cards[e],s,o)}},updateTransformStyle=(t,e,s)=>{let o="rotateX("+e+"deg) rotateY("+s+"deg)";t.style.transform=o,t.style.webkitTransform=o,t.style.mozTransform=o,t.style.msTransform=o,t.style.oTransform=o},removeTransformStyle=t=>{t.style.transform="",t.style.webkitTransform="",t.style.mozTransform="",t.style.msTransform="",t.style.oTransform=""};function updateCardPerspective(t){isTimeToUpdate()&&update(t)}window.addEventListener("mousemove",updateCardPerspective);